package com.hp.hpl.jena.util;
import org.apache.log4j.*;
import org.apache.log4j.varia.NullAppender;
import org.apache.log4j.spi.LoggingEvent;

/**
 * 
 * This class is a utility for use with the log4j
 * library, during testing.
 * It suppresses a specific Logger, and then reports
 * whether any messages were generated by that Logger.
 * @author jjc
 *
 */
public class TestLogger {
    private int eCnt = 0;
    /** Suppresses the logger identified by c
     * 
     * @param c The class naming the Logger to suppress.
     */
    public TestLogger(Class c) {
        logger = Logger.getLogger(c);
        app = new NullAppender() {
                public void doAppend(LoggingEvent e){
                    eCnt++;
                }
            };
        logger.setAdditivity(false);
        logger.addAppender(app);
    }
    private Appender app;
    private Logger logger;
    /**
     * Ends suppression of the logger.
     * @throws NullPointerException If invoked twice.
     * @return boolean True if there were no errors.
     */
    public boolean end() {
       logger.setAdditivity(true);
       logger.removeAppender(app);
       logger = null;
       return eCnt==0;
    }
}
